mapper.select:
select DRL from RULE_INFO
where GROUP_ID = #{groupId,jdbcType=VARCHAR} and ARTIFACT_ID = #{artifactId,jdbcType=VARCHAR} and
RULE_VERSION = #{version,jdbcType=VARCHAR}

mapper.delete:
delete from RULE_INFO
where GROUP_ID = #{groupId,jdbcType=VARCHAR} and ARTIFACT_ID = #{artifactId,jdbcType=VARCHAR} and
RULE_VERSION = #{version,jdbcType=VARCHAR}

mapper.select:
{
    call update_rule_status(#{groupId, jdbcType=VARCHAR, mode=IN}, #{artifactId, jdbcType=VARCHAR, mode=IN},
        #{version, jdbcType=VARCHAR, mode=IN})
}

mapper.update:
update RULE_INFO set STATUS = 0
where GROUP_ID = #{groupId,jdbcType=VARCHAR} and ARTIFACT_ID = #{artifactId,jdbcType=VARCHAR} and
      RULE_VERSION = #{version,jdbcType=VARCHAR}
